import { useState } from "react"

const initialUserInput = {
  'current-savings': 10000,
  'yearly-contribution': 1200,
  'expected-return': 7,
  duration: 10,
};
const UserInput = (props) => {
   
    const [userInput, setUserInput] = useState(initialUserInput)

    const submitHandler = (e) => {
        e.preventDefault()
        props.onCalculate(userInput)
    

    const resetHandler = (e) => {
      setUserInput(initialUserInput)
    
  }
  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => ({
      ...prevInput,
      [input]: +value,
    }));
  };

    }
   
  
   

  

    return (
        <form className="form" onSubmit={submitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={e => inputChangeHandler('current-savings', e.target.value)}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={e => inputChangeHandler('yearly-contribution', e.target.value)}/>
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={e => inputChangeHandler('number', e.target.value)}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={e => inputChangeHandler('duration', e.target.value)}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt" onClick={(e) => resetHandler}>
            Reset
          </button>
          <button type="submit" className="button" onClick={e => submitHandler('current-savings', e.target.value)}>
            Calculate
          </button>
        </p>
      </form>

    )
}

export default UserInput
